import React, { useState } from 'react';
import './Projects.css';
import getfile from './imgs/getfile.jpeg';
import gupload from './imgs/gupload.jpeg';

const Projects = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredProjects, setFilteredProjects] = useState([]);

  const projects = [
    {
      id: 1,
      title: 'Project 1',
      description: 'The Book Cloud Tool project is a web-based file management system...',
      tools: ['HTML/CSS', 'JavaScript', 'PHP'],
      images: [gupload, getfile, getfile],
      link: 'http://get-your-file.rf.gd/index.php',
    },
    // Add more project objects here
  ];

  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = projects.filter((project) =>
      project.title.toLowerCase().includes(query)
    );
    setFilteredProjects(filtered);
  };

  const renderProjects = (projectList) => {
    return projectList.map((project) => (
      <div className="col-md-4" key={project.id}>
        <div className="project-card">
          <div className="project-image">
            <img src={project.images[0]} alt={project.title} />
          </div>
          <div className="project-details">
            <h3 className="project-title">{project.title}</h3>
            <p className="project-description">{project.description}</p>
            <div className="project-info">
              <h4 className="info-title">Tools:</h4>
              <ul className="info-list">
                {project.tools.map((tool, index) => (
                  <li key={index}>{tool}</li>
                ))}
              </ul>
            </div>
            <div className="project-images">
              {project.images.slice(1).map((image, index) => (
                <img src={image} alt={`${project.title} Image ${index + 2}`} key={index} />
              ))}
            </div>
            <a href={project.link} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
              Visit Project
            </a>
          </div>
        </div>
      </div>
    ));
  };

  const renderedProjects = searchQuery ? renderProjects(filteredProjects) : renderProjects(projects);

  return (
    <section className="projects-section">
      <div className="container">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="search-bar">
              <input
                type="text"
                className="form-control"
                placeholder="Search projects..."
                value={searchQuery}
                onChange={handleSearch}
              />
            </div>
          </div>
        </div>
        <h2 className="section-title">My Projects</h2>
        <div className="row">{renderedProjects}</div>
      </div>
      <footer className="bg-dark text-white text-center py-3">
        <p>&copy; 2023 My Portfolio. All rights reserved.</p>
      </footer>
    </section>
  );
};

export default Projects;
