import React from 'react';
import { Link } from 'react-router-dom';
import { Element, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll';
import myImage from './imgs/sardar.jpeg';
import getfile from './imgs/getfile.jpeg';
import './Home.css';

const Home = () => {


    //////////////////////////////////
// Wrap the JavaScript code inside a function
function initializeGame() {
    // Get the canvas element and its 2D rendering context
    const canvas = document.getElementById('game-canvas');
    const context = canvas.getContext('2d');
  
    // Initialize drawing variables
    let isDrawing = false;
    let lastX = 0;
    let lastY = 0;
  
    // Start drawing
    function startDrawing(e) {
      isDrawing = true;
      [lastX, lastY] = [e.offsetX, e.offsetY];
    }
  
    // Stop drawing
    function stopDrawing() {
      isDrawing = false;
    }
  
    // Draw a line
    function drawLine(e) {
      if (!isDrawing) return;
      context.beginPath();
      context.moveTo(lastX, lastY);
      context.lineTo(e.offsetX, e.offsetY);
      context.stroke();
      [lastX, lastY] = [e.offsetX, e.offsetY];
    }
  
    // Call the initializeGame function after the DOM has loaded
    document.addEventListener('DOMContentLoaded', initializeGame);
  }
  

    ///////////////////////////////////
    const backgroundStyle = {
        backgroundImage: `url('https://media.istockphoto.com/id/1475344702/photo/background-image-of-computers-and-laptops-with-green-code-lines.webp?b=1&s=170667a&w=0&k=20&c=8911R9u7RIm3tk9SGKUhDmrA6ci8GJ5Cj9-Rc0gLUJ8=')`, // Replace with your desired background image URL
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    };
    React.useEffect(() => {
        scrollSpy.update();
    }, []);

    const scrollTo = (elementName) => {
        scroller.scrollTo(elementName, {
            duration: 800,
            delay: 0,
            smooth: 'easeInOutQuart',
        });
    };

    return (
        <div>
            <header className="bg-primary text-white text-center py-5">
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                            <Element name="photo">
                                <img
                                    src={myImage}
                                    className="img-fluid rounded-circle animate__animated animate__fadeInLeft"
                                    alt="Your Photo"
                                />
                            </Element>
                        </div>
